/**
 * 获得是否可以播放广告及剩余次数
 * @param {func} callback 回调函数
 * @return boolean 是否可播放
 */
window.h5api.canPlayAd(callback);

/**
 * 广告状态回调函数
 * @param {object} data 状态
 */
function callback(data) {
  console.log("是否可播放广告", data.canPlayAd, "剩余次数", data.remain);
  Event.attributes.剩余次数=data.remain
}

/**
 * 播放全屏广告
 * @param callback   播放广告时的广告状态回调函数
 */
window.h5api.playAd(callback);

/**
 * 此callback回调函数的形式
 *
 * @param obj  广告状态
 */
function callback(obj) {
  console.log("代码:" + obj.code + ",消息:" + obj.message);
  if (obj.code === 10000) {
    console.log("开始播放");
  } else if (obj.code === 10001) {
    console.log("播放结束");
    let 增加的值=Variable.get("421d3f3f0149412c")+0.5
    console.log(Variable.get("421d3f3f0149412c"))//获取全局变量
    Variable.set("421d3f3f0149412c",增加的值) //给全局对象赋值
  } else {
    console.log("广告异常");
  }
}

